<?php
defined( 'ABSPATH' ) or die( 'Access denied.' );

add_action( 'admin_menu', 'add_massage_menu' );

function add_massage_menu() {
	add_menu_page( 'Massage Booking', 'Massage Booking', 'manage_options', 'sabai-salud-massage-booking', 'display_massage_menu', 'data:image/svg+xml;base64,' );
}

function display_massage_menu(){
	if ( !current_user_can( 'manage_options' ) )  {
		wp_die( __( 'You do not have sufficient permissions to access this page.' ) );
	}
	echo '<div class="wrap">';
	echo '<p>Options.</p>';
	echo '</div>';
}
?>
